import numpy as npimport jax.numpy as jnpimport matplotlib.pyplot as pltnom_amas='A644'n_R500=2.5rebin_method='transfert'with open("covar/"+nom_amas+"_covar_"+rebin_method+"_"+str(n_R500)+"R500.npy","rb") as file:    covar = jnp.load(file)print('Taille covariance : ',str(jnp.shape(covar)))inv_covar=jnp.linalg.inv(covar)fig, axes = plt.subplots(ncols=2,nrows=1)im=axes.flat[0].imshow(covar,interpolation=None,vmax=1e-12,vmin=-1e-12)axes.flat[0].set_title(r'$\Sigma$')axes.flat[1].imshow(inv_covar,interpolation=None)axes.flat[1].set_title(r'$\Sigma^{-1}$')fig.subplots_adjust(right=0.8)cbar_ax = fig.add_axes([0.85, 0.15, 0.05, 0.7])fig.colorbar(im, cax=cbar_ax)plt.title(nom_amas)plt.show()